for name in "${!images[@]}"; do
    url="${images[$name]}"
    if [[ ! -f "$name" ]]; then
        echo "Downloading $name..."
        wget -U "Mozilla/5.0" -O "$name" "$url"
        sleep 0.5
    else
        echo "Skipping $name, already exists."
    fi
done
